<?phprequire_once '../lal/lal.appIdentity.php';class module_union_members {	function update_unionMember($union_Id, $user_Id, $roleName, $isAdmin) {	/* FUNCTION DESCRIPTION : Check Member already exists in Table or not 							   1. IF EXISTS, update Information							   2. IF DOESN'T EXISTS, add Information	 */		$inputArray=array();		$inputArray["union_Id"]=$union_Id;		$inputArray["user_Id"]=$user_Id;		$outputArray='*';		$extraString='';		$memObj=new union_mem();		$jsonData=$memObj->getunion_memData($inputArray, $outputArray, $extraString);		$dejsonData=json_decode($jsonData);		if(count($dejsonData)>0) { /* Exists in Table */			$setArray=array();			$setArray["roleName"]=$roleName;			$setArray["isAdmin"]=$isAdmin;			$conditionArray=array();			$conditionArray["union_Id"]=$union_Id;			$conditionArray["user_Id"]=$user_Id;			$memObj->updateunion_memData($setArray, $conditionArray);		} 		else { /* Doesn't Exists in Table */			$idObj=new identity();			$member_Id=$idObj->union_mem_id();			$addedOn=date("Y-m-d H:i:s");			$memObj->addunion_memData($member_Id,$union_Id,$user_Id,$roleName,$isAdmin,$addedOn);		}	}}?>